import { useEffect, useState } from 'react';
import Logout from "../components/Logout";
import { useUser } from "../context/UserContext";
import { useNavigate } from "react-router-dom";
import { useAlert } from '../context/AlertContext';

export default function LogoutPage() {
    const { user } = useUser();
    const navigate = useNavigate();
    const { addAlert } = useAlert();
    const [alertShown, setAlertShown] = useState(false); // State to track if alert has been shown

    useEffect(() => {
        // Check if the user is not logged in and the alert hasn't been shown yet
        if (user.username === undefined && !alertShown) {
            addAlert('You haven\'t logged in yet', 'info');
            setAlertShown(true); // Update the state to indicate alert has been shown
            navigate('/login');
        }
    }, [user.username, addAlert, navigate, alertShown]);

    if (user.username !== undefined) {
        // Only render the Logout component if the user is logged in
        return <Logout />;
    }

    // If user is not logged in, nothing else to render
    return null;
}
