// src/components/Alerts.js
import { useAlert } from '../context/AlertContext';
import './Alerts.css'

export default function Alerts() {
    const { alerts } = useAlert();

    return (
        alerts.length > 0 && (
            <div className="alert-overlay">
                {alerts.map((msg, index) => (
                    <div key={index} className="alert-card">
                        {msg}
                    </div>
                ))}
            </div>
        )
    );
}
