import React, { useState } from 'react';
import '../styles/SignupForm.css';
import { useAlert } from '../context/AlertContext';
import {useNavigate} from "react-router-dom";

const SignupForm = () => {
   const [user, setUser] = useState({});
   const { addAlert } = useAlert();
   const navigate = useNavigate();

const handleSignUp = async (e) => {
  e.preventDefault(); // Prevent the form from refreshing the page

  try {
    const response = await fetch('http://localhost:8000/api/signup/', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(user), // Assuming 'user' is { email, password }
    });

    if (!response.ok) {
      throw new Error('User Already Exists'); // Handle login failure
    }

    const data = await response.json();
    console.log('SignUp successful!', data);
    addAlert('SignUp successful!', 'success');

    // Store tokens in localStorage
    localStorage.setItem('access_token', data.access);
    localStorage.setItem('refresh_token', data.refresh);
    navigate('/', {state: {'user' : user}});

    // Optionally redirect to a protected page
    // window.location.href = "/dashboard";
  } catch (error) {
    addAlert(error.message, 'error');
    console.error('Login error:', error.message);
  }
};

  return (
    <form className="form-container">
      <h2>Signup</h2>
      <form onSubmit={handleSignup}>
        <input
          type="text"
          placeholder="Username"
          value={user.username}
          onChange={handleChange}
        />
        <input
          type="email"
          placeholder="Email"
          value={user.email}
          onChange={handleChange}
        />
        <input
          type="password"
          placeholder="Password"
          value={user.password}
          onChange={handleChange}
        />
        <button type="submit" onSubmit={handleSignUp}>Signup</button>
      </form>
    </form>
  );
};

export default SignupForm;
