import { useEffect, useRef } from 'react';
import Logout from "../components/Logout";
import { useUser } from "../context/UserContext";
import { useNavigate } from "react-router-dom";
import { useAlert } from '../context/AlertContext';

export default function LogoutPage() {
    const { user } = useUser();
    const navigate = useNavigate();
    const { addAlert } = useAlert();
    const alertShownRef = useRef(false); // Ref to track if alert has been shown

    useEffect(() => {
        // Only add the alert if the user is not logged in and the alert hasn't been shown yet
        if (user.username === undefined && !alertShownRef.current) {
            addAlert('You haven\'t logged in yet', 'info');
            alertShownRef.current = true; // Set the ref to true after showing the alert
            navigate('/login');
        }
    }, [user.username, addAlert, navigate]);

    return (
        <Logout />
    );
}
