import './App.css';
import Sidebar from "./components/Sidebar";
import AppRoutes from './routes/AppRoutes';
import {BrowserRouter, createPath} from 'react-router-dom';
import Alerts from './components/Alerts';
import React from'react';
import { AlertProvider } from './context/AlertContext';
import { UserProvider } from './context/UserContext'; // Import UserProvider
function App() {

    const [user, setUser] = React.useState({});
    React.useEffect(() => {
        const storedUser = localStorage.getItem('user');
        if (storedUser) {
            setUser({ username: storedUser }); // Set the user as an object
        }
        console.log('USER UPDATED', storedUser);
    }, []);


  return (
    <BrowserRouter>
      <AlertProvider>
          <UserProvider>
              <div style={{display: 'flex'}}>
                <Sidebar user={user}/>
                <div style={{marginLeft: '200px', width: '100%'}}  > {/* Adjust margin based on Sidebar width */}
                    <AppRoutes />
                </div>
              </div>
            </UserProvider>
        <Alerts />
      </AlertProvider>

    </BrowserRouter>
  );
}

export default App;
